tunnels-casing tunnels-fill
(select way,highway from &prefix;_line where highway in ('motorway','motorway_link','trunk','trunk_link','primary','primary_link','secondary','secondary_link','tertiary','tertiary_link','residential','unclassified','minor') and tunnel in ('yes','true','1') order by z_order) as roads
&datasource-settings;
&layer-citywall;
turning_circle-casing
(select p.way as way,l.highway as int_tc_type from &prefix;_point p join &prefix;_line l on ST_DWithin(p.way,l.way,&dwithin_node_way;) where p.highway='turning_circle' and l.highway in ('tertiary','unclassified','residential','service','living_street') ) as turning_circle
&datasource-settings;
footbikecycle-tunnels
(select way,highway,horse,foot,bicycle from &prefix;_line where highway in ('bridleway','footway','cycleway','path') and tunnel in ('yes','true','1') order by z_order) as roads
&datasource-settings;
tracks-tunnels
(select way,tracktype from &prefix;_line where highway='track' and tunnel in ('yes','true','1')) as tracks
&datasource-settings;
&layer-buildings;
cliffs barriers
(select way,barrier,"natural",man_made from &prefix;_line where barrier is not null or "natural" in ('hedge','cliff') or man_made='embankment') as roads
&datasource-settings;
barriers
(select way,barrier,"natural" from &prefix;_polygon where barrier is not null or "natural"='hedge') as barriers
&datasource-settings;
highway-area-casing
(select way,highway,railway from &prefix;_polygon where highway in ('residential','unclassified','pedestrian','service','footway','track','path','platform') or railway='platform' order by z_order,way_area desc) as roads
&datasource-settings;
minor-roads-casing-links minor-roads-casing
(select way,highway,service, case when tunnel in ('yes','true','1') then 'yes'::text else tunnel end as tunnel from &prefix;_line where highway in ('motorway','motorway_link','trunk','trunk_link','primary','primary_link','secondary','secondary_link','tertiary','tertiary_link','residential','unclassified','minor','road','service','pedestrian','raceway','living_street') order by z_order) as roads
&datasource-settings;
turning_circle-fill
(select p.way as way,l.highway as int_tc_type from &prefix;_point p join &prefix;_line l on ST_DWithin(p.way,l.way,&dwithin_node_way;) where p.highway='turning_circle' and l.highway in ('tertiary','unclassified','residential','service','living_street') ) as turning_circle
&datasource-settings;
highway-area-fill
(select way,highway,railway from &prefix;_polygon where highway in ('residential','unclassified','pedestrian','service','footway','living_street','track','path','platform') or railway='platform' order by z_order,way_area desc) as roads
&datasource-settings;
tracks-notunnel-nobridge
(select way,tracktype from &prefix;_line where highway='track' and (bridge is null or bridge in ('no','false','0')) and (tunnel is null or tunnel in ('no','false','0'))) as tracks
&datasource-settings;
minor-roads-fill-links minor-roads-fill
(select way,highway,horse,bicycle,foot,construction,aeroway,service, case when tunnel in ('yes','true','1') then 'yes'::text else tunnel end as tunnel, case when bridge in ('yes','true','1') then 'yes'::text else bridge end as bridge, case when railway in ('spur','siding') or (railway='rail' and service in ('spur','siding','yard')) then 'spur-siding-yard'::text else railway end as railway from &prefix;_line where highway is not null or aeroway in ('runway','taxiway') or railway in ('light_rail','narrow_gauge','funicular','rail','subway','tram','spur','siding','platform','disused','abandoned','construction') order by z_order) as roads
&datasource-settings;
&layer-ferry-routes; &layer-aerialways;
roads
(select way,highway,railway, case when tunnel in ('yes','true','1') then 'yes'::text else tunnel end as tunnel from &prefix;_roads where highway is not null or (railway is not null and (service is null or service not in ('spur','siding','yard'))) order by z_order ) as roads
&datasource-settings;
waterway-bridges
(select way,name from &prefix;_line where waterway='canal' and bridge in ('yes','true','1','aqueduct') order by z_order) as water &datasource-settings;
road-bridges-casing road-bridges-fill noncased-ways-bridges primarybridge_layer0_casing mwaybridge_layer0_casing primarybridge_layer0_fill mwaybridge_layer0_fill primarybridge_layer1_casing mwaybridge_layer1_casing primarybridge_layer1_fill mwaybridge_layer1_fill primarybridge_layer2_casing mwaybridge_layer2_casing primarybridge_layer2_fill mwaybridge_layer2_fill mwaybridge_layer3_casing mwaybridge_layer3_fill mwaybridge_layer4_casing mwaybridge_layer4_fill mwaybridge_layer5_casing mwaybridge_layer5_fill
(select way,highway,aeroway,railway,layer,horse,bicycle,foot,bridge from &prefix;_line where (highway is not null or aeroway in ('runway','taxiway') or railway in ('light_rail','subway')) and bridge not in ('no','false','0') order by z_order ) as roads
&datasource-settings;
tracks-bridges
(select way,tracktype from &prefix;_line where highway='track' and bridge in ('yes','true','1')) as tracks
&datasource-settings;
access
(select way,access,highway from &prefix;_line where access is not null) as access
&datasource-settings;
trams
(select way from &prefix;_line where railway='tram' and (tunnel is null or tunnel not in ('yes','true','1'))) as trams
&datasource-settings;
guideways
(select way from &prefix;_line where highway='bus_guideway' and (tunnel is null or tunnel not in ('yes','true','1'))) as guideways
&datasource-settings;
&layer-admin; &layer-placenames; &layer-amenity-stations; &layer-amenity-symbols; &layer-amenity-points; &layer-power;
directions
(select way, case when oneway in ('yes','true','1') then 'yes'::text else oneway end as oneway from &prefix;_line where oneway is not null and (highway is not null or railway is not null or waterway is not null) ) as directions
&datasource-settings;
roads-text-low-zoom
(select way,highway,ref,char_length(ref) as length from &prefix;_roads where highway in ('motorway','trunk','primary') and ref is not null ) as roads
&datasource-settings;
highway-junctions
(select way,ref,name from &prefix;_point where highway='motorway_junction' ) as junctions
&datasource-settings;
roads-text
(select way,highway,aeroway,name,ref,char_length(ref) as length, case when bridge in ('yes','true','1') then 'yes'::text else bridge end as bridge from &prefix;_line where waterway IS NULL and leisure IS NULL and landuse IS NULL and (name is not null or ref is not null) ) as roads
&datasource-settings;
text
(select way,amenity,shop,access,leisure,landuse,man_made,"natural",place,tourism,ele,name,ref,military,aeroway,waterway,historic,'yes'::text as point from &prefix;_point where amenity is not null or shop in ('supermarket','bakery','clothes','fashion','convenience','doityourself','hairdresser','department_store', 'butcher') or leisure is not null or landuse is not null or tourism is not null or "natural" is not null or man_made in ('lighthouse','windmill') or place='island' or military='danger_area' or aeroway='gate' or waterway='lock' or historic='memorial' ) as text
&datasource-settings;
text
(select way,aeroway,shop,access,amenity,leisure,landuse,man_made,"natural",place,tourism,NULL as ele,name,ref,military,waterway,historic,'no'::text as point from &prefix;_polygon where amenity is not null or shop in ('supermarket','bakery','clothes','fashion','convenience','doityourself','hairdresser','department_store', 'butcher') or leisure is not null or landuse is not null or tourism is not null or "natural" is not null or man_made in ('lighthouse','windmill') or place='island' or military='danger_area' or historic='memorial' ) as text
&datasource-settings;
area-text
(select way,way_area,name from &prefix;_polygon where name is not null and (waterway is null or waterway <> 'riverbank') order by z_order,way_area desc ) as text
&datasource-settings;
&layer-addressing;
boundary
(select way,name,boundary from &prefix;_polygon where boundary='national_park') as boundary
&datasource-settings;
theme_park
(select way,name,tourism from &prefix;_polygon where tourism='theme_park') as theme_park
&datasource-settings;
